import React from 'react';
import './App.css';

function App() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);

  const handleSelectCategory = (categoryId) => {
    setSelectedCategory(categoryId);
    setSelectedSubcategory(null);
  };

  const handleSelectSubcategory = (subcategoryId) => {
    setSelectedSubcategory(subcategoryId);
  };

  // Sample data for categories, subcategories, and products
  const categories = [
    { id: 1, name: 'Category 1' },
    { id: 2, name: 'Category 2' },
    { id: 3, name: 'Category 3' },
  ];

  const subcategories = [
    { id: 1, name: 'Subcategory 1', categoryId: 1 },
    { id: 2, name: 'Subcategory 2', categoryId: 1 },
    { id: 3, name: 'Subcategory 3', categoryId: 2 },
    { id: 4, name: 'Subcategory 4', categoryId: 2 },
    { id: 5, name: 'Subcategory 5', categoryId: 3 },
  ];

  const products = [
    { id: 1, name: 'Product 1', description: 'Description 1', price: 10, subcategoryId: 1 },
    { id: 2, name: 'Product 2', description: 'Description 2', price: 20, subcategoryId: 1 },
    { id: 3, name: 'Product 3', description: 'Description 3', price: 30, subcategoryId: 1 },
    { id: 4, name: 'Product 4', description: 'Description 4', price: 40, subcategoryId: 4 },
    { id: 5, name: 'Product 5', description: 'Description 5', price: 50, subcategoryId: 5 },
  ];

  // Filter subcategories and products based on the selected category and subcategory
  const filteredSubcategories = subcategories.filter(
    (subcategory) => subcategory.categoryId === selectedCategory
  );

  const filteredProducts = products.filter(
    (product) => product.subcategoryId === selectedSubcategory
  );

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to the Marketplace</h1>
        <p>Discover unique products from around the world.</p>
      </header>
      <main className="App-content">
        <section className="Product-list">
          <div className="Product-card">
            <img
              className="Product-image"
              src="https://example.com/product1.jpg"
              alt="Product 1"
            />
            <div className="Product-details">
              <h2>Product 1</h2>
              <p>Description of Product 1</p>
              <button className="Product-button">View Details</button>
            </div>
          </div>
          <div className="Product-card">
            <img
              className="Product-image"
              src="https://example.com/product2.jpg"
              alt="Product 2"
            />
            <div className="Product-details">
              <h2>Product 2</h2>
              <p>Description of Product 2</p>
              <button className="Product-button">View Details</button>
            </div>
          </div>
          {/* Add more product cards as needed */}
        </section>
      </main>
      <footer className="App-footer">
        <p>&copy; 2023 Your Marketplace. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
